var Jimp = global.get('Jimp');

//       
var imageBuffer = msg.payload;

Jimp.read(imageBuffer)
    .then(image => {
        // Convert image to grayscale
        image.grayscale();

        // Calculate the average brightness
        let totalBrightness = 0;
        let count = 0;
        for (let y = 0; y < image.bitmap.height; y++) {
            for (let x = 0; x < image.bitmap.width; x++) {
                let color = Jimp.intToRGBA(image.getPixelColor(x, y));
                let brightness = (color.r + color.g + color.b) / 3;
                totalBrightness += brightness;
                count++;
            }
        }

        let avgBrightness = totalBrightness / count;
        msg.payload = `Average Brightness: ${avgBrightness}`;
        node.send(msg);
    })
    .catch(err => {
        node.error(err, msg);
    });

